import React, { useState } from "react";
import ReactDOM from "react-dom";
import {
  Menu,
  Dropdown,
  Space,
  Input,
  Button,
  Form,
  Table,
  Image,
  Select,
} from "antd";
import "draft-js/dist/Draft.css";
import "./stepFirst.css";
const { Option } = Select;

const StepFirst = ({
  dataForAddProduct,
  setDataForAddProduct,
  lstCategory,
}) => {
  const [form] = Form.useForm();
  const [srcImageProduct, setSrcImageProduct] = useState(
    dataForAddProduct.link
  );
  const handleChangeDataInput = (event) => {
    // console.log(dataForAddProduct)
    setDataForAddProduct({
      ...dataForAddProduct,
      [event.target.name]: event.target.value,
    });
  };

  const handleUpdateImgProduct = (event) => {
    const [file] = event.target.files;
    setDataForAddProduct({
      ...dataForAddProduct,
      fileImage: file,
    });
    console.log(file);
    console.log(dataForAddProduct.link);
    setSrcImageProduct(URL.createObjectURL(file));
  };

  return (
    <div className="container-step1">
      <Form layout="inline" form={form}>
        <div className="container-item">
          <span className="css-label">Tên sản phẩm *</span>
          <Input
            placeholder="nhập tên"
            className="css-input-item"
            value={dataForAddProduct.name}
            name="name"
            onChange={handleChangeDataInput}
          />
        </div>
        <div className="container-item">
          <span className="css-label">Thương hiệu *</span>
          <Input
            placeholder="nhập thương hiệu"
            className="css-input-item"
            value={dataForAddProduct.brand}
            name="brand"
            onChange={handleChangeDataInput}
          />
        </div>
        <div className="container-item">
          <span className="css-label">Đơn vị tính *</span>
          <Input
            placeholder="nhập địa chỉ chi tiết"
            className="css-input-item"
            value={dataForAddProduct.packingForm}
            name="packingForm"
            onChange={handleChangeDataInput}
          />
        </div>
        <div className="container-item">
          <span className="css-label">Danh mục sản phẩm *</span>
          {/* <Input
            placeholder="nhập danh mục sản phẩm"
            className="css-input-item"
            value={dataForAddProduct.categoryId}
            name="categoryId"
            onChange={handleChangeDataInput}
          /> */}
          <Select
            showSearch
            className="css-input-select"
            placeholder="Search to Select"
            optionFilterProp="children"
            filterOption={(input, option) => option.children.includes(input)}
            filterSort={(optionA, optionB) =>
              optionA.children
                .toLowerCase()
                .localeCompare(optionB.children.toLowerCase())
            }
            onSelect={(event) => {
              setDataForAddProduct({ ...dataForAddProduct, categoryId: event });
            }}
          >
            {lstCategory.map((value, index) => {
              return (
                <Option key={index} value={value.id}>
                  {value.name}
                </Option>
              );
            })}
          </Select>
        </div>
        <div className="container-item">
          <span className="css-label">Đường dẫn tài liệu *</span>
          <Input
            placeholder="nhập đường dẫn tài liệu"
            className="css-input-item"
            value={dataForAddProduct.productAsset}
            name="productAsset"
            onChange={handleChangeDataInput}
          />
        </div>
        <div className="container-item">
          <span className="css-label">Mô tả sản phẩm *</span>
          <Input.TextArea
            type="text"
            className="css-text-area"
            id="description"
            name="description"
            placeholder="nhập mô tả"
            onChange={handleChangeDataInput}
            value={dataForAddProduct.description}
            // onChange={(event) => handleInputChange(index, event)}
            // style={{ minHeight: 128, width: 800 }}

            // defaultValue={""}
          />
        </div>
        <div className="container-item" style={{ width: 800 }}>
          <span className="css-label">Chọn hình ảnh *</span>
          {/* <image
            class="custom-avt"
            width={200}
            src="https://gw.alipayobjects.com/zos/rmsportal/KDpgvguMpGfqaHPjicRK.svg"
            fallback="data:image/png;base64,"
          >
            <div class="overlay">
              <button>CAMERA</button>
            </div>
          </image> */}
          <label htmlFor="imgProduct">
            <img
              className="css-image"
              src={
                srcImageProduct ||
                "data:image/png;base64,"
              }
              alt=""
            />
            <input
              type="file"
              id="imgProduct"
              style={{ display: "none" }}
              onChange={handleUpdateImgProduct}
            />
          </label>
        </div>
      </Form>
    </div>
  );
};

export default StepFirst;
